$(document).bind("mobileinit",function(){$.mobile.pushStateEnabled=true});$(function(){var e;$("a.showMenu").click(function(){if(e!=true){$(".ui-page-active").animate({marginLeft:"165px"},300,function(){e=true});return false}else{$(".ui-page-active").animate({marginLeft:"0px"},300,function(){e=false});return false}});$("#menu, .pages").live("swipeleft",function(){if(e){$(".ui-page-active").animate({marginLeft:"0px"},300,function(){e=false})}});$(".pages").live("swiperight",function(){if(!e){$(".ui-page-active").animate({marginLeft:"165px"},300,function(){e=true})}});$('div[data-role="page"]').live("pagebeforeshow",function(t,n){e=false;$(".pages").css("margin-left","0")});$("#menu li a").click(function(){var e=$(this).parent();if($(e).hasClass("active")){$("#menu li").removeClass("active")}else{$("#menu li").removeClass("active");$(e).addClass("active")}})})